package user

// !!!WARNING!!! It is a autogenerated file!

import (
	"encoding/json"
	"github.com/google/go-querystring/query"
	"github.com/otokarev/mt5tk/pkg/client"
	"github.com/otokarev/mt5tk/pkg/connection"
	"github.com/otokarev/mt5tk/pkg/model/entities"
	"log"
	"strconv"
)

type User struct {
	Client     *client.Client
	ClientPool []*client.Client
}

type getRequest struct {
	Id string `url:"login"`
}

type getResponse struct {
	Answer entities.User `json:"answer"`
}

type totalResponse struct {
	Answer struct {
		Total string `json:"total"`
	} `json:"answer"`
}

func (g *User) GetTotal() (int, error) {
	payload, err := g.Client.Get("/api/user/total")
	if err != nil {
		return 0, err
	}
	resp := totalResponse{}
	if nil != json.Unmarshal(payload, &resp) {
		return 0, err
	}
	total, err := strconv.Atoi(resp.Answer.Total)
	if nil != err {
		return 0, err
	}

	return total, nil
}

func (g *User) Add(data entities.User) (entities.User, error) {
	body, err := json.Marshal(data)
	if err != nil {
		return entities.User{}, err
	}
	resultBytes, err := g.Client.Post("/api/user/add", body)
	if err != nil {
		return entities.User{}, err
	}

	var result entities.User
	err = json.Unmarshal(resultBytes, &result)
	if err != nil {
		return entities.User{}, err
	}

	return result, err
}

func (g *User) Exists(name string) (bool, error) {
	_, err := g.Get(name)
	if err == nil {
		return true, nil
	}

	if cerr, ok := err.(*connection.Error); ok == true && cerr.IsNotFound() == false {
		log.Fatalf("cannot verify user %s existance, error: %s", name, err.Error())
		return false, cerr
	}

	return false, nil
}

func (g *User) Get(name string) (entities.User, error) {
	req := getRequest{Id: name}
	q, err := query.Values(req)
	if err != nil {
		return entities.User{}, err
	}

	payload, err := g.Client.Get("/api/user/get?" + q.Encode())
	if err != nil {
		return entities.User{}, err
	}
	resp := getResponse{}
	if nil != json.Unmarshal(payload, &resp) {
		return entities.User{}, err
	}

	return resp.Answer, nil
}
